import { Router } from "express";
import { getAllUser, loginJwt, logout, register, getUserById, updateUserStatusById, forgetPassword, sendOtp, verifyUserOtp } from "../controllers/user.controller.js";
import { verifyUser } from "../middlewares/authUser.middleware.js";
const router = Router();
router.route('/register').post(register);
router.route('/login').post(loginJwt);
router.route('/').post(verifyUser, logout);
router.route('/').get(getAllUser);
router.route('/:userId').get(getUserById);
router.route('/forget-password').post(forgetPassword);
router.route('/status/:userId').patch(updateUserStatusById);
router.route('/send-otp').post(sendOtp);
router.route('/verify-otp').post(verifyUserOtp);
export default router;
